import * as httpMock from '../../mocks/http';
import { getAuthenticationHeaders } from '../../../src/utils/auth';
import { ConversationsApi } from '../../../src/api/conversations';
import { testJwt } from '../../mocks/jwt';
import { buildTestHeaders } from '../testHeadersBuilder';
import packageInfo from '../../../package.json';

describe('Conversations API', () => {
    const serviceUrl = 'http://some-url.com';
    const userId = 'user-id';
    const authHeaders = getAuthenticationHeaders({
        jwt: testJwt(),
    });
    let httpSpy;
    let api;

    beforeEach(() => {
        httpSpy = httpMock.mock();
        api = new ConversationsApi({
            serviceUrl,
            authHeaders,
            VERSION: packageInfo.version,
        });
    });

    afterEach(() => {
        httpMock.restore();
    });

    describe('#get', () => {
        it('should call http', () => {
            return api.get(userId).then(() => {
                const fullUrl = `${serviceUrl}/v1.1/appusers/${userId}/conversation`;
                httpSpy.should.have.been.calledWith('GET', fullUrl, undefined, buildTestHeaders(authHeaders));
            });
        });
    });

    describe('#postPostback', () => {
        it('should call http', () => {
            const body = {
                actionId: 'action-id',
            };

            return api.postPostback(userId, body.actionId).then(() => {
                const fullUrl = `${serviceUrl}/v1.1/appusers/${userId}/conversation/postback`;
                httpSpy.should.have.been.calledWith('POST', fullUrl, body, buildTestHeaders(authHeaders));
            });
        });
    });

    describe('#resetUnreadCount', () => {
        it('should call http', () => {
            return api.resetUnreadCount(userId).then(() => {
                const fullUrl = `${serviceUrl}/v1.1/appusers/${userId}/conversation/read`;
                httpSpy.should.have.been.calledWith('POST', fullUrl, undefined, buildTestHeaders(authHeaders));
            });
        });
    });
});
