import * as httpMock from '../../mocks/http';
import { getAuthenticationHeaders } from '../../../src/utils/auth';
import { IntegrationMenuApi } from '../../../src/api/integrationMenu';
import { testJwt } from '../../mocks/jwt';
import { buildTestHeaders } from '../testHeadersBuilder';
import packageInfo from '../../../package.json';

describe('Integration Menu API', () => {
    const serviceUrl = 'http://some-url.com';
    const noPropsMessage = 'Must provide props.';
    const noItemsMessage = 'Must provide an array of items.';
    const authHeaders = getAuthenticationHeaders({
        jwt: testJwt(),
    });
    const scope = 'account';
    const appId = 'appid_12345';
    const integrationId = 'integrationid_12345';
    let httpSpy;
    let api;

    beforeEach(() => {
        httpSpy = httpMock.mock();
        api = new IntegrationMenuApi({
            serviceUrl,
            authHeaders,
            scope,
            VERSION: packageInfo.version,
        });
    });

    afterEach(() => {
        httpMock.restore();
    });

    describe('#get', () => {
        it('should call http', () => {
            return api.get(appId, integrationId).then(() => {
                const fullUrl = `${serviceUrl}/v1.1/apps/${appId}/integrations/${integrationId}/menu`;
                httpSpy.should.have.been.calledWith('GET', fullUrl, undefined, buildTestHeaders(authHeaders));
            });
        });
    });

    describe('#create', () => {
        const props = {
            items: [],
        };

        it('should call http', (done) => {
            api.create(appId, integrationId, props).then(() => {
                const fullUrl = `${serviceUrl}/v1.1/apps/${appId}/integrations/${integrationId}/menu`;
                httpSpy.should.have.been.calledWith('POST', fullUrl, props, buildTestHeaders(authHeaders));
                done();
            });
        });

        it('should return an error if no items', () => {
            return expect(() => api.create(appId, integrationId, {})).to.throw(noItemsMessage);
        });

        it('should return an error if no props', () => {
            return expect(() => api.create(appId, integrationId, undefined)).to.throw(noPropsMessage);
        });
    });

    describe('#update', () => {
        const props = {
            items: [],
        };

        it('should call http', () => {
            return api.update(appId, integrationId, props).then(() => {
                const fullUrl = `${serviceUrl}/v1.1/apps/${appId}/integrations/${integrationId}/menu`;
                httpSpy.should.have.been.calledWith('PUT', fullUrl, props, buildTestHeaders(authHeaders));
            });
        });

        it('should return an error if no items', () => {
            return expect(() => api.update(appId, integrationId, {})).to.throw();
        });

        it('should return an error if no props', () => {
            expect(() => api.update(appId, integrationId, undefined)).to.throw(noPropsMessage);
        });
    });

    describe('#delete', () => {
        it('should call http', () => {
            return api.delete(appId, integrationId).then(() => {
                const fullUrl = `${serviceUrl}/v1.1/apps/${appId}/integrations/${integrationId}/menu`;
                httpSpy.should.have.been.calledWith('DELETE', fullUrl, undefined, buildTestHeaders(authHeaders));
            });
        });
    });
});
